glsl3.vert:
precision highp float;
uniform CCGlobal {
  mat4 cc_matView;
  mat4 cc_matViewInv;
  mat4 cc_matProj;
  mat4 cc_matProjInv;
  mat4 cc_matViewProj;
  mat4 cc_matViewProjInv;
  vec4 cc_cameraPos;
  vec4 cc_time;
  mediump vec4 cc_screenSize;
  mediump vec4 cc_screenScale;
};
uniform CCLocal {
  mat4 cc_matWorld;
  mat4 cc_matWorldIT;
};
in vec3 a_position;
in vec4 a_color;
out vec4 v_color;
#if USE_TEXTURE
in vec2 a_uv0;
out vec2 v_uv0;
#endif
void main () {
  vec4 pos = vec4(a_position, 1);
  #if CC_USE_MODEL
  pos = cc_matViewProj * cc_matWorld * pos;
  #else
  pos = cc_matViewProj * pos;
  #endif
  #if USE_TEXTURE
  v_uv0 = a_uv0;
  #endif
  v_color = a_color;
  gl_Position = pos;
}

glsl3.frag:
precision highp float;
#if USE_ALPHA_TEST
  uniform ALPHA_TEST {
    float alphaThreshold;
  };
#endif
void ALPHA_TEST (in vec4 color) {
  #if USE_ALPHA_TEST
      if (color.a < alphaThreshold) discard;
  #endif
}
void ALPHA_TEST (in float alpha) {
  #if USE_ALPHA_TEST
      if (alpha < alphaThreshold) discard;
  #endif
}
in vec4 v_color;
#if USE_TEXTURE
in vec2 v_uv0;
uniform sampler2D texture;
#endif
uniform sampler2D textureNoise;
uniform lamyoung_com{
  vec4 burnColorInner;
  vec4 burnColorOut;
  float noiseThreshold;
  float colorWidth;
};
vec3 lerp(vec3 a, vec3 b, float t){
  return a+(b-a)*t;
}
void main () {
  vec4 noise = vec4(1, 1, 1, 1);
  vec4 textureNoise_tmp = texture(textureNoise, v_uv0*3.0);
  #if CC_USE_ALPHA_ATLAS_textureNoise
      textureNoise_tmp.a *= texture(textureNoise, v_uv0*3.0 + vec2(0, 0.5)).r;
  #endif
  #if INPUT_IS_GAMMA
    noise.rgb *= (textureNoise_tmp.rgb * textureNoise_tmp.rgb);
    noise.a *= textureNoise_tmp.a;
  #else
    noise *= textureNoise_tmp;
  #endif
  if(noise.r < noiseThreshold){
    discard;
  }
  vec4 o = vec4(1, 1, 1, 1);
  #if USE_TEXTURE
  vec4 texture_tmp = texture(texture, v_uv0);
  #if CC_USE_ALPHA_ATLAS_texture
      texture_tmp.a *= texture(texture, v_uv0 + vec2(0, 0.5)).r;
  #endif
  #if INPUT_IS_GAMMA
    o.rgb *= (texture_tmp.rgb * texture_tmp.rgb);
    o.a *= texture_tmp.a;
  #else
    o *= texture_tmp;
  #endif
  #endif
  float t = 1.0 - smoothstep(0.0, colorWidth, noise.r - noiseThreshold);
  vec3 burnColor = lerp(burnColorInner.rgb, burnColorOut.rgb, t);
  o.rgb = lerp(o.rgb, burnColor.rgb,  t*step(0.0001, noiseThreshold ));
  o *= v_color;
  ALPHA_TEST(o);
  gl_FragColor = o;
}

